CREATE TABLE USER (ID INTEGER PRIMARY KEY, UUID TEXT UNIQUE NOT NULL, EMAIL TEXT NOT NULL UNIQUE, EXTERNAL_ID TEXT, PROVIDER TEXT NOT NULL, STATUS TEXT NOT NULL, DATE_CREATED TEXT NOT NULL) STRICT;
CREATE TABLE TAPE (ID INTEGER PRIMARY KEY, USER_ID INTEGER REFERENCES USER (ID) NOT NULL, TITLE TEXT NOT NULL, DESC TEXT, STATUS TEXT NOT NULL, STATUS_MSG TEXT, DATE_CREATED TEXT NOT NULL, DATE_UPDATED TEXT) STRICT;
CREATE TABLE TAPE_SOURCE (ID INTEGER PRIMARY KEY, TAPE_ID INTEGER REFERENCES TAPE (ID) NOT NULL, SEQ INTEGER NOT NULL, TYPE TEXT NOT NULL, URL TEXT NOT NULL, FILE_EXTENSION TEXT, CONTENT_TYPE TEXT, CONTENT TEXT, DATE_CREATED TEXT NOT NULL, DATE_UPDATED TEXT) STRICT;